import React from 'react';
import styled, { css } from 'styled-components/macro';
import { Button } from './Button';
import { IoMdArrowRoundForward } from 'react-icons/io';



const HeroSection = styled.section`
height: 100vh;
max-height: 1100px;
position: relative;
overflow: hidden;
`;

const HeroWrapper = styled.div`
width: 100%;
height: 100%;
display: flex;
justify-content: center;
align-items: center;
overflow: hidden;
position: relative;
`;

const HeroSlide = styled.div``;
const HeroSlider = styled.div``;
const HeroImage = styled.img``;
const HeroContent = styled.div``;
const Arrow = styled(IoMdArrowRoundForward)``;

const Hero = ({ slides }) => {
    return (
    <HeroSection>
        <HeroWrapper>
            {slides.map((slide, index) => {
                return (
                  <HeroSlide key={index}>
                   <HeroSlider>
                     <HeroImage />
                     <HeroContent>
                       <h1>{slide.title}</h1>
                       <p>{slide.message}</p>
                       <Button 
                       to = {slide.path}
                        primary='true'
                        css={`
                        max-width: 160px;
                        `}
                        >

                           {slide.label}
                           <Arrow />
                       </Button>
                     </HeroContent>
                   </HeroSlider>
                  </HeroSlide>

                )
            })}
        </HeroWrapper>
    </HeroSection>
);
}

export default Hero
